css:
/*
based: Solarized theme for code-mirror
http ://ethanschoonover.com/solarized
*/
/*
Generated color on
https://material.io/tools/color/
https://material.io/design/color/the-color-system.html#tools-for-picking-colors
*/
.editor {
  /* Color scheme for code-mirror */
  /* Editor styling */
  /* Little shadow on the view-port of the buffer view */
  /* Gutter border and some shadow from it  */
  /* Gutter colors and line number styling based of color scheme (dark / light) */
  /* Dark */
  /* Common */
  /*
  Active line. Negative margin compensates left padding of the text in the
  view-port
  */
}
.editor .CodeMirror {
  color-profile: sRGB;
  rendering-intent: auto;
  color: #e0e0e0;
  background-color: #2e3235;
  /* Active Line Highlight support */
}
.editor .CodeMirror .cm-header {
  color: #facf4e;
}
.editor .CodeMirror .cm-formatting-header {
  opacity: 0.5;
}
.editor .CodeMirror .cm-atom {
  color: #56c8d8;
}
.editor .CodeMirror .cm-number {
  color: #ffad42;
}
.editor .CodeMirror .cm-property {
  color: #facf4e;
}
.editor .CodeMirror .cm-def {
  color: #fa5788;
}
.editor .CodeMirror .cm-operator {
  color: #56c8d8;
}
.editor .CodeMirror .cm-tag {
  color: #fa5788;
}
.editor .CodeMirror .cm-keyword {
  color: #cf6edf;
}
.editor .CodeMirror .cm-list-indent ~ .cm-keyword,
.editor .CodeMirror .cm-formatting-list ~ .cm-keyword {
  color: inherit;
}
.editor .CodeMirror .cm-variable-2.cm-formatting-list-ul {
  color: #facf4e;
}
.editor .CodeMirror .cm-variable-3.cm-formatting-list-ul {
  color: #ff6e40;
}
.editor .CodeMirror .cm-keyword.cm-formatting-list-ul {
  color: #6abf69;
}
.editor .CodeMirror .cm-formatting-list-ol {
  color: #7186f0;
}
.editor .CodeMirror .cm-formatting.cm-formatting-task.cm-meta {
  color: #a0a4ae;
}
.editor .CodeMirror .cm-comment {
  color: #707d8b;
}
.editor .CodeMirror .cm-keyword.cm-inline-code,
.editor .CodeMirror .cm-inline-code {
  color: #bdbdbd;
}
.editor .CodeMirror .cm-formatting-code,
.editor .CodeMirror .cm-formatting-code-block {
  color: #a0a4ae;
}
.editor .CodeMirror .cm-quote {
  color: #cf6edf;
  font-style: italic;
}
.editor .CodeMirror .cm-string {
  color: #99d066;
}
.editor .CodeMirror .cm-string-2 {
  color: #4ebaaa;
}
.editor .CodeMirror .cm-meta {
  color: #707d8b;
}
.editor .CodeMirror .cm-qualifier {
  color: #cf6edf;
}
.editor .CodeMirror .cm-builtin {
  color: #cf6edf;
}
.editor .CodeMirror .CodeMirror-matchingbracket {
  color: #e0e0e0;
  outline: solid 1px #4ebaaa;
}
.editor .CodeMirror .CodeMirror-nonmatchingbracket {
  color: #ff5f52;
}
.editor .CodeMirror .cm-attribute {
  color: #cf6edf;
}
.editor .CodeMirror .CodeMirror-widget {
  text-shadow: none;
}
.editor .CodeMirror .cm-hr {
  color: #56c8d8;
}
.editor .CodeMirror .cm-table-sep,
.editor .CodeMirror .table-row.table-row-1 {
  color: #a0a4ae;
}
.editor .CodeMirror .cm-url {
  cursor: pointer;
}
.editor .CodeMirror .cm-link {
  text-decoration: none;
}
.editor .CodeMirror .cm-keyword.cm-link,
.editor .CodeMirror .cm-link,
.editor .CodeMirror .cm-url {
  color: #4ebaaa;
}
.editor .CodeMirror .cm-string.cm-url {
  color: #4ebaaa;
  opacity: 0.7;
  cursor: pointer;
}
.editor .CodeMirror .cm-keyword.cm-em,
.editor .CodeMirror .cm-em {
  font-style: italic;
  color: #99d066;
}
.editor .CodeMirror .cm-keyword.cm-strong,
.editor .CodeMirror .cm-strong {
  color: #ff6e40;
}
.editor .CodeMirror .cm-formatting-strong,
.editor .CodeMirror .cm-formatting-em {
  opacity: 0.6;
}
.editor .CodeMirror .cm-special {
  color: #cf6edf;
}
.editor .CodeMirror .cm-error,
.editor .CodeMirror .cm-invalidchar {
  color: #ff5f52;
  border-bottom: 1px dotted #ff5f52;
}
.editor .CodeMirror .cm-whitespace::before {
  color: #a0a4ae;
  opacity: 0.4;
}
.editor .CodeMirror .CodeMirror-code > div > pre > span::after,
.editor .CodeMirror .CodeMirror-line > span::after {
  color: #a0a4ae;
  opacity: 0.4;
}
.editor .CodeMirror .CodeMirror-selected {
  background-color: #505d64;
}
.editor .CodeMirror .CodeMirror-placeholder {
  color: #707d8b;
}
.editor .CodeMirror .CodeMirror-activeline .CodeMirror-activeline-background,
.editor .CodeMirror .CodeMirror-activeline .CodeMirror-activeline-gutter {
  background-color: rgba(112, 125, 139, 0.13) !important;
}
.editor .CodeMirror .CodeMirror-activeline .CodeMirror-linenumber {
  color: #e0e0e0;
}
.editor .CodeMirror {
  -moz-box-shadow: inset 7px 0 12px -6px #000;
  -webkit-box-shadow: inset 7px 0 12px -6px #000;
  box-shadow: inset 7px 0 12px -6px #000;
}
.editor .CodeMirror .CodeMirror-gutters {
  border-right: 1px solid;
}
.editor .CodeMirror .CodeMirror-gutters {
  background-color: #2e3235;
  border-color: #4f5b66;
}
.editor .CodeMirror .CodeMirror-linenumber {
  color: #606f7a;
  padding: 0 5px;
}
.editor .CodeMirror .CodeMirror-guttermarker-subtle {
  color: #707d8b;
}
.editor .CodeMirror .CodeMirror-guttermarker {
  color: #ddd;
}
.editor .CodeMirror .CodeMirror-gutter .CodeMirror-gutter-text {
  color: #707d8b;
}
.editor .CodeMirror .CodeMirror-cursor {
  border-left: 2px solid #ffe9a0;
}
.editor .CodeMirror .CodeMirror-activeline-background {
  background: rgba(255, 255, 255, 0.1);
}
.editor .cm-searching {
  border: 1px solid #facf4e;
  padding: 2px;
  color: white !important;
  background: transparent;
}
.editor .CodeMirror .CodeMirror-linewidget img.inline-widget {
  background: rgba(255, 255, 255, 0.05);
}
.editor .cm-inline-code:not(.cm-formatting-code),
.editor .gfm-codeblock-bg:not(.gfm-codeblock-begin-bg):not(.gfm-codeblock-end-bg) {
  background: rgba(0, 0, 0, 0.15);
}
.editor .cm-inline-code:not(.cm-formatting-code) {
  border: solid 1px rgba(255, 255, 255, 0.15);
  border-radius: 0.25em;
  padding: 0.1em 1px;
  margin: 0 -2px;
}
body:not(.platform-darwin) .editor .CodeMirror-vscrollbar {
  background: #2e3235;
}
